import React, { useState } from 'react';
import './home.css'

function DataManagement() {
    const [data, setData] = useState([]);
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [salary, setSalary] = useState('');

    const AddData = () => {
        if (name && email && salary) {
            setData([...data, { name, email, salary }]);
            setName('');
            setEmail('');
            setSalary('');

        }
    };

    const handleDeleteData = (index) => {
        const newData = [...data];
        newData.splice(index, 1);
        setData(newData);
    };

    return (
        <center>
            <div className="container">
                <h1>Add Data</h1>
                <div className="text-light form-data">
                    <table>
                        <div className="form-group">
                            <label>Name:</label>
                            <input
                                className='ml-3'
                                type="text"
                                value={name}
                                onChange={(e) => setName(e.target.value)}
                            />
                        </div>
                        <div className="form-group">
                            <label>Email : </label>
                            <input
                                className='ml-3'
                                type="text"
                                value={email}
                                onChange={(e) => setEmail(e.target.value)}
                            />
                        </div>
                        <div className="form-group">
                            <label>Salary:</label>
                            <input
                                className='ml-3'
                                type="text"
                                value={salary}
                                onChange={(e) => setSalary(e.target.value)}
                            />
                        </div>
                        <button className="btn btn-primary" onClick={ () => AddData()}>Add Data</button>
                    </table>
                </div>
                <div>
                    <table className="table-data w-10 container">
                        <thead>
                            <tr>
                                <th>Name</th>
                                <th>Email</th>
                                <th>Salary</th>
                                <th>Action</th>
                            </tr>
                        </thead>
                        <tbody>
                            {data.map((item, index) => (
                                <tr key={index}>
                                    <td>{item.name}</td>
                                    <td>{item.email}</td>
                                    <td>{item.salary}</td>
                                    <td>
                                        <button className="btn btn-danger" onClick={() => handleDeleteData(index)}>Delete</button>
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>

            </div>
        </center>
    );
}

export default DataManagement;
