import { useState } from "react";
import './home.css'

const Record = () => {

    const [input, setInput] = useState([
        { id: '', name: '', phone: '' }
    ])

    const addData = () => {
        let newRow = { id: Math.floor(Math.random() * 10000), name: '', phone: '' };
        let add = [...input, newRow];
        setInput(add)
    }

    const inputhandle = (e, i) => {
        let data = [...input];
        data[i][e.target.name] = e.target.value;
        setInput(data)
    }

    const remove = (id) => {
        let allData = [...input];
        let del = allData.filter((val) => {
            return val.id != id;
        })
        setInput(del)
    }

    return (
        <div className="conmain">
            <h1>Dynamic Form</h1>
            <form>
                {
                    input.map((item, i) => {
                        return (
                            <div key={i}>
                                <input type="hidden" value={item.id} />
                                Name : <input type="text" name="name" value={item.name} onChange={(e) => inputhandle(e, i)} />
                                Phone : <input type="text" name="phone" value={item.phone} onChange={(e) => inputhandle(e, i)} />
                                {
                                    i == 0 ? "" : <button type="button" onClick={() => remove(item.id)}>
                                        {
                                            i == 0 ? "" : "delete"
                                        }
                                    </button>
                                }
                            </div>
                        )
                    })
                }
            </form>

            <button onClick={() => addData()}>Add</button>
        </div>
    )
}

export default Record;
