import React, { useState } from "react";
import "./cnter.css"; 

function cnter() {
  const [cnt, setcnt] = useState(0);

  const increment = () => {
    setcnt(cnt + 1);
  };

  const decrement = () => {
    if (cnt > 0) {
      setcnt(cnt - 1);
    }
  };

  const reset = () => {
    setcnt(0);
  };

  return (
    <div className="cnter-container">
      <h1>cntER APP</h1>
      <div className="cnter">
        <button onClick={()=> decrement()}>-</button>
        <span>{cnt}</span>
        <button onClick={()=> increment()}>+</button>
      </div>
      <button onClick={reset}>Reset</button>
    </div>
  );
}

export default cnter;
