import {  useState } from "react";

const Dynamicform = () => {

    const [input,setInput] = useState([
        {id : '',name : '',phone : ''}
    ])
    const [record,setRecord] = useState([]);

   

    const AddMore = () => {
        let newdata = {id : Math.floor(Math.random()*10000),name : '',phone : ''};
        let olddata = [...input,newdata];
        setInput(olddata)
    }

    const handleChange = (event,index) => {
        let data = [...input];
        data[index][event.target.name] = event.target.value;
        setInput(data)
    }

    const Insert = () => {
       localStorage.setItem('user',JSON.stringify(input))
       setInput({
            name : '',phone : ''
       })
    }

    const remove = (id) => {
        let all = [...input];
        let d = all.filter((val)=>{
            return val.id != id;
        })
        setInput({
            name : '',
            phone : ''
        })
        setInput(d)
    }

    useEffect(()=>{
        let all = JSON.parse(localStorage.getItem('user')) ? JSON.parse(localStorage.getItem('user')) : [];
        setRecord(all)
    },[])

    return (
        <center>
            <h2>Dynamic Form</h2>
            <form>
               {
                    input.map((item,index)=>{
                        return (
                            <div key={index}>
                                <input type="hidden" value={item.id}/>
                                Name : <input type="text" name="name" value={item.name} onChange={ (event) => handleChange(event,index) }/>
                                Phone : <input type="text" name="phone" value={item.phone} onChange={ (event) => handleChange(event,index) }/>
                               {
                                    index == 0 ? ""  :  <button type="button" onClick={ () => remove(item.id) }>{index == 0 ?  "" : "Remove"}</button>
                               }
                            </div>
                        )
                    })
               }
            </form>

            <button onClick={ () => AddMore() }>Add</button> <br></br>
            <button onClick={ () => Insert() }>Insert</button> <br></br><br></br>


            <table border="1">
               <tr>
                    <td>Name</td>
                    <td>Phone</td>
               </tr>

                {
                    record.map((val)=>{
                        return (
                            <tr>
                                <td>{val.name}</td>
                                <td>{val.phone}</td>
                            </tr>
                        )
                    })
                }

            </table>


        </center>
    )
}

export default Dynamicform;
