import { useState } from "react";

const Record = () => {

    const [input, setInput] = useState([
        { id: '', name: '', phone: '' }
    ])

    const addData = () => {
        let newRow = { id: Math.floor(Math.random() * 10000), name: '', phone: '' };
        let add = [...input, newRow];
        setInput(add)
    }

    const inputhandle = (e, i) => {
        let data = [...input];
        data[i][e.target.name] = e.target.value;
        setInput(data)
    }

    const remove = (id) => {
        let allData = [...input];
        let del = allData.filter((val) => {
            return val.id != id;
        })
        setInput(del)
    }

    return (
        <div class="container">
        <h1>Dynamic Form</h1>
        <form id="dynamic-form">
            {
                input.map((item, i) => {
                    return (
                        <div class="form-group" key={i}>
                            <input type="hidden" value={item.id} />
                            <label for={"name-" + i}>Name:</label>
                            <input type="text" id={"name-" + i} class="form-control" name="name" value={item.name} onChange={(e) => inputhandle(e, i)} />
                            <label for={"phone-" + i}>Phone:</label>
                            <input type="text" id={"phone-" + i} class="form-control" name="phone" value={item.phone} onChange={(e) => inputhandle(e, i)} />
                            {
                                i === 0 ? "" : <button type="button" class="btn btn-danger" onClick={() => remove(item.id)}>Delete</button>
                            }
                        </div>
                    )
                })
            }
        </form>
        <button class="btn btn-primary" onClick={() => addData()}>Add</button>
    </div>
    )
}

export default Record;
