import { useState } from "react";
import './home.css'

const Record = () => {

    const [input, setInput] = useState([
        { id: '', name: '', phone: '' }
    ])

    const addData = () => {
        let newRow = { id: Math.floor(Math.random() * 10000), name: '', phone: '' };
        let add = [...input, newRow];
        setInput(add)
    }

    const inputhandle = (e, i) => {
        let data = [...input];
        data[i][e.target.name] = e.target.value;
        setInput(data)
    }

    const remove = (id) => {
        let allData = [...input];
        let del = allData.filter((val) => {
            return val.id != id;
        })
        setInput(del)
    }

    return (
        <div className="container main">
            <div>
                <h1>Dynamic Form</h1>
                <table className="table-data w-10 container">
                        <thead>
                            <tr>
                                <th>Name</th>
                                <th>Email</th>
                                <th>Salary</th>
                                <th>Action</th>
                            </tr>
                        </thead>
                        <tbody>
                            {data.map((item, i) => (
                                <tr key={i}>
                                    <td>{item.name}</td>
                                    <td>{item.email}</td>
                                    <td>{item.salary}</td>
                                    <td>
                                        <button className="btn btn-danger" onClick={() => deleteData(i)}>Delete</button>
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                    {
                        input.map((item, i) => {
                            return (
                                <div key={i}>
                                    <input type="hidden" value={item.id} />
                                    Name : <input type="text" name="name" value={item.name} onChange={(e) => inputhandle(e, i)} />
                                    Phone : <input type="text" name="phone" value={item.phone} onChange={(e) => inputhandle(e, i)} />
                                    {
                                        i == 0 ? "" : <button type="button" className="btn btn-danger m-2 p-1" onClick={() => remove(item.id)}>
                                            {
                                                i == 0 ? "" : "delete"
                                            }
                                        </button>
                                    }
                                </div>
                            )
                        })
                    }

                <button className="btn btn-primary m-2 p-2" onClick={() => addData()}>Add</button>
            </div>
        </div>
    )
}

export default Record;
