import { useEffect, useState } from "react";

const Dynamicform = () => {

    const [input,setInput] = useState([
        {name : '',phone : '',salary : ''}
    ])   

    const AddMore = () => {
        let newdata = {name : '',phone : '',salary: ''};
        let olddata = [...input,newdata];
        setInput(olddata)
    }

    const remove = (id) => {
        let all = [...input];
        let d = all.filter((val)=>{
            return val.id != id;
        })
        setInput({
            name : '',
            phone : ''
        })
        setInput(d)
    }

    return (
        <center>
            <h2>Dynamic Form</h2>
